    	double mean = 0.0;
    	for (i = 0; i &lt; size; i++ ){
	    	sum += pow(data[i][0], 2) + pow(data[i][1], 2);
	    }

    	mean = sum / size;
    	return sqrt(mean);
    }

    int FindHighestPeakIndex(fftw_complex *f, int qty)
