    		if (sqrt(f[index][0]* f[index][0] + f[index][1]* f[index][1]) &lt; sqrt(f[i + 1][0] * f[i + 1][0] + f[i + 1][1] * f[i + 1][1]) ) {
	    		index = i + 1;
	    	}
