        new RSVP.Promise(function (parentResolve) { // no control over this
            console.log(&#39;1&#39;);

            // my code here..
            var p = new RSVP.Promise(function (innerResolve) {
               setTimeout(function () {
                   console.log(&#39;3&#39;);
                   innerResolve();
               }, 5000);
            });
            
            // Wrap it in an object
           parentResolve({result: p});
        }).then(function () {
