    public class Routing
        {
            static Frame AppRouting;
    
            public Routing(Frame appRouting)
            {
                AppRouting = appRouting;
            }
    
            public static bool IsInitialized()
            {
                return AppRouting != null;
            }
    
            public static Route[] Routes { get; } = new Route[] 
            {
                new Route(){ Path=&quot;login&quot;, Component=typeof(Login) }
            };
    
    
            public static void Navigate(string path)
            {
                var matchingRoutes = Routes.Where(x =&gt; x.Path == path);
    
                if (matchingRoutes.Count() != 0)
                {
                    var route = matchingRoutes.First();
                    AppRouting.Navigate(route.Component);
                }
                else
                {
                    throw new Exception($&quot;Path &#39;{path}&#39; not found!&quot;);
                }
            }
        }


