        if (mFusedLocationClient == null)
            return;
        try {
            // mFusedLocationClient is of type FusedLocationProviderClient,
            // initialized in the onCreate method of the Activity
            final Task&lt;Void&gt; removeUpdatesTask = mFusedLocationClient.removeLocationUpdates(mLocationCallback);
            removeUpdatesTask.addOnCompleteListener(new OnCompleteListener&lt;Void&gt;() {
                @Override
                public void onComplete(@NonNull Task&lt;Void&gt; task) {
                    Log.e(TAG, &quot;addOnCompleteListener: &quot;+task.isComplete());
                }
            });

            removeUpdatesTask.addOnSuccessListener(new OnSuccessListener&lt;Void&gt;() {
                @Override
                public void onSuccess(Void aVoid) {
                    Log.e(TAG, &quot;addOnSuccessListener fired &quot; );
                }
            });

            removeUpdatesTask.addOnFailureListener(new OnFailureListener() {
                @Override
                public void onFailure(@NonNull Exception e) {
                    Log.e(TAG, &quot;addOnFailureListener fired &quot;);
                }
            });
            if (removeUpdatesTask.isSuccessful()) {
                Log.v(TAG, &quot;Stop Location Updates successful.&quot;);
            }
            else {
                Log.e(TAG, &quot;Stop Location Updates was NOT successful.&quot; + removeUpdatesTask.toString());
            }

        }
        catch (SecurityException ex) {
            Log.d(TAG, &quot;Security exception occurred while stopping location updates: &quot; + ex.getLocalizedMessage());
        }
    }

