                    onBlur={(event: Event) =&gt; this.formatAmount(event)}
                    validation={[
                        isRequired(t(&#39;validation&#39;)(&#39;amountRequired&#39;)),
                        number(t(&#39;validation&#39;)(&#39;amountNotNumber&#39;)),
