So, so far I made this:

    // Find minimum x value so that x + d = y
    template&lt;typename T&gt;
    T subtractMost (const T y, const T d)
    {
        T x = y - d;
        while (true)
        {
            const T nextX = std::nextafter (x, -std::numeric_limits&lt;T&gt;::infinity());
            if (nextX + d != y)
                return x;
            T step = x - nextX;
            while (true)
            {
                const T nextStep = step + step;
                if (x - nextStep + d != y)
                    break;
                step = nextStep;
            }
            x -= step;
        }
    }

