    grouping:
      type: FIELDS
      args: [&quot;url&quot;]
      streamId: &quot;status&quot;

  - from: &quot;index&quot;
    to: &quot;status&quot;
    grouping:
      type: FIELDS
      args: [&quot;url&quot;]
      streamId: &quot;status&quot;  
      
  - from: &quot;parse&quot;
    to: &quot;redirection_bolt&quot;
    grouping:
      type: LOCAL_OR_SHUFFLE
        
  - from: &quot;redirection_bolt&quot;
    to: &quot;parser_bolt&quot;
    grouping:
      type: LOCAL_OR_SHUFFLE

  - from: &quot;redirection_bolt&quot;
    to: &quot;index&quot;
    grouping:
      type: LOCAL_OR_SHUFFLE
 
  - from: &quot;parser_bolt&quot;
    to: &quot;index&quot;
    grouping:
      type: LOCAL_OR_SHUFFLE
