    insert into my_table (label, document) values (&#39;white&#39;, &#39;{&quot;name&quot;: &quot;john&quot;, &quot;lastname&quot;: &quot;doe&quot;, &quot;eyes&quot;: &quot;brown&quot;, &quot;birth&quot;: &quot;07/08/1979 19:12:55&quot;, &quot;pet&quot;: &quot;dinosour&quot;, &quot;wife&quot;: &quot;872&quot;, &quot;cat&quot;: &quot;no&quot;}&#39;);
