    bool CSerialComm::Receive(LPSTR ReadBufData, int len)
    {
    
     DWORD ErrorFlags;
     DWORD Err;
    
     DWORD BytesRead;
     COMSTAT ComStat;
     
     ClearCommError(m_hComm,&amp;ErrorFlags,&amp;ComStat);
    
     BytesRead = ComStat.cbInQue;
     

    
     if (BytesRead &gt; 0) 
     {
      if(!ReadFile(m_hComm, &amp;ReadBufData, len, &amp;BytesRead, &amp;m_OldR))
      {
       if(GetLastError() == ERROR_IO_PENDING)
       {
