Update: The following ended up working! 

    handleFormSubmit(data) {
        const {
            importUserToClassroom,
            importUsers,
            addFlashMessage,
            formatErrors,
            showUiBlocked,
            hideUiBlocked,
            match: {params: {classroomId}},
        } = this.props;
        showUiBlocked();
        const users = userDataToList(data);
        if (!isEmpty(classroomId)) {
            importUserToClassroom({
                users,
                classroomId,
            })
                .then(() =&gt; {
                    hideUiBlocked();
                    addFlashMessage(&#39;Users imported successfully&#39;, &#39;success&#39;);
                    this.context.router.history.push(&#39;/users&#39;);
                })
                .catch(err =&gt; {
                    hideUiBlocked();
                    if (err.status !== 409) {
                        return formatErrors(err).then(err =&gt; {
                            this.setState({
                                errors: err,
                            });
                        });
                    } else {
                        this.setState({
                            errors: {
                                usernames: err.data.message,
                            }
                        });
                    }
                });
        } else {
            importUsers({users})
                .then(() =&gt; {
                    hideUiBlocked();
                    addFlashMessage(&#39;Users imported successfully&#39;, &#39;success&#39;);
                    this.context.router.history.push(&#39;/users&#39;);
                })
                .catch(err =&gt; {
                    hideUiBlocked();
                    if (err.status !== 409) {
                        return formatErrors(err).then(err =&gt; {
                            this.setState({
                                errors: err,
                            });
                        });
                    } else {
                        this.setState({
                            errors: {
                                usernames: err.data.message,
                            }
                        });
                    }
                });
        }
    }

