    NTSTATUS create_directory(UNICODE_STRING dirName) {
	IO_STATUS_BLOCK iosb;
	OBJECT_ATTRIBUTES ObjectAttributes;
	NTSTATUS Status;
	HANDLE FileHandle = NULL;
	FILE_OBJECT *FileObj;

	//DbgPrint(&quot;Directory :- &#39;%wZ&#39;&quot;, &amp;dirName);

	InitializeObjectAttributes(
