          fprintf(stderr, &quot;%s: snprintf(): %s\n&quot;, program, strerror(errno));
          return &quot;&quot;;
        }
        return user;
      }
    
      cache_uid = pwd-&gt;pw_uid;
      cache_pw_name = pwd-&gt;pw_name;
    
      return pwd-&gt;pw_name;
    }

