                    // What does NOT work============
                    CarType src = CarTypes[SelectedIndex];
                    CarType newDto = _mapper.Map&lt;CarType&gt;(src); &lt;=== fails here
                    _carTypeDto = newDto;

                    // Incorrect Solution #1 ======================
