		fmt.Println(call2(foo, &quot;hello&quot;))
	}
	
	func foo(x string) string {
		return x + &quot;!&quot;
	}
	
	func call1(f, x interface{}) interface{} {
		fValue := reflect.ValueOf(f)
		inValue := reflect.New(reflect.TypeOf(x)).Elem()
		inValue.Set(reflect.ValueOf(x))
	
