    MObject	node;
    for (; !iter.isDone(); iter.next())
    {
    	MSelectionList setMemberlist;
    	iter.getDependNode(node);
    	MFnSet fnSet(node);
    	fnSet.getMembers(setMemberlist, false);
    	if (setMemberlist.length() == 0)
