    	MDagPath		 pathDag;
    	MItSelectionList setIter(setMemberlist);
    	for (; !setIter.isDone(); setIter.next())
    	{
    		setIter.getDagPath(pathDag);
    		MGlobal::displayInfo(&quot;DagPath Full Path  Name: &quot; + MString() + pathDag.fullPathName());
    	}
    }

